public class RepeatedStringMatch {
    public static int repeatedStringMatch(String A, String B) {
        StringBuilder sb = new StringBuilder();
        while (sb.length() < B.length()) {
            sb.append(A);
        }
        int times = sb.length() / A.length();
        if (sb.toString().contains(B)) return times;
        sb.append(A);
        if (sb.toString().contains(B)) return times + 1;
        return -1;
    }

    public static void main(String[] args) {
        String A = "aba";
        String B = "babbbbaabbababbaaaaababbaaabbbbaaabbbababbbbabaabababaabaaabbbabababbbabababaababaaaaabbabaaaabaaaab";

        String A1 = "aa";
        String B1 = "a";

        String A2 = "a";
        String B2 = "aa";

        String A3 = "abcd";
        String B3 = "cdabcdab";

        String A4 = "a";
        String B4 = "";


        //System.out.println(repeatedStringMatch(A, B));
        //System.out.println(repeatedStringMatch(A1, B1));
        //System.out.println(repeatedStringMatch(A2, B2));
        //System.out.println(repeatedStringMatch(A3, B3));
        System.out.println(repeatedStringMatch(A4, B4));

    }
}
